import React from 'react';
import { useRouter } from 'next/router';

function ActiveLink({ children, href }) {
  const router = useRouter();
  const style = {
    marginRight: 10,
    color: router.pathname === href ? 'red' : 'black',
  };

  return (
    <a href={href} style={style}>
      {children}
    </a>
  );
}

export default ActiveLink
