import React from 'react';
import { IconContext } from 'react-icons';
import { GrTwitter, GrInstagram, GrFacebook } from 'react-icons/gr';
import styles from './Header.module.css';

const handleClick = (e) => {
  e.target.classList.add('active')
};

export default function Header() {
  return (
    <>
      <header id="header">
        <div className="top-section">
          <div className="number">
            <span>(52) 55 054 548 064</span>
          </div>
          <div className="social-media">
            <IconContext.Provider
              value={{ className: styles.reactIcon }}
            >
              <a href="https://www.facebook.com">
                <GrFacebook />
              </a>
              <a href="https://www.facebook.com">
                <GrTwitter />
              </a>
              <a href="https://www.facebook.com">
                <GrInstagram />
              </a>
            </IconContext.Provider>
          </div>
        </div>
        <div className="bottom-section">
          <img src="/static/logoHorizontal.jpg" alt="Logo Horizontal" />
          <nav className="menu">
            <ul>
              <li><a onClick={(e) => handleClick(e)} href="#header">Inicio</a></li>
              <li><a onClick={() => handleClick()} href="#servicios">Servicios</a></li>
              <li><a onClick={() => handleClick()} href="#nosotros">Nosotros</a></li>
              <li><a onClick={() => handleClick()} href="#contacto">Contacto</a></li>
            </ul>
          </nav>
        </div>
      </header>
      <style jsx>
        {`
        header{
          background-color: var(--second-brand-color);
          height: 40px;
        }
        .top-section{
          height: 40px;
          display: flex;
          justify-content: space-around;
          align-items: center;
          padding-left: 50px;
          color: white;
        }
        .social-media{
          display: flex;
          align-items: center;
          height: 100%;
        }
        a{
          display: flex;
        }
        .bottom-section{
          display: flex;
        }
        .bottom-section img{
          height: 90px;
          display: inline;
        }
        .menu{
          width: 100%;
        }
        .menu ul{
          height: 100%;
          margin: 0;
          display: flex;
          justify-content: space-around;
          list-style: none;
        }
        li a {
          height: 100%;
          width: 100%;
          display: flex;
          justify-content: center;
          align-items: center;
          text-decoration: none;
        }
        li a:active {
          background-color: var(--first-brand-color);
          color: white;
        }
        li {
          display: flex;
          justify-content: center;
          align-items: center;
          background-color: white;
          color: black;
          width: 150px;
        }
      `}
      </style>
    </>
  );
}
